import React from "react";
import { Button } from "@nextui-org/react";
import kfupmlogo from "assets/"

const SideBar = () => {
  return (
    <div className="bg-white z-0 min-w-72 max-w-72 relative">
      <aside className="h-full border-r border-gray-200 flex flex-col">
        <div className="p-4 h-24 sm:h-16 flex items-center justify-center">
          <div className="font-bold text-xl text-kfupmgreen">KFUPM <span className="text-kfupmdarkgray">Clinic</span></div>
        </div>

        <div className="flex flex-col justify-between flex-1">
          <div className="space-y-8">
            <div className="space-y-2 p-2 pt-8">

              <div className="pl-6 pt-2 pr-6 space-y-4 max-w-fit">
                {/* Buttons with animation */}
                <Button
                  ripple={true}
                  className="font-normal text-base min-w-full max-w-full bg-transparent text-gray-700 relative"
                >
                  Schedule Appointment
                </Button>
                <Button
                  ripple={true}
                  className="font-normal text-base min-w-full max-w-full bg-transparent text-gray-700 relative"
                >
                  Tasks
                </Button>
                <Button
                  ripple={true}
                  className="font-normal text-base min-w-full max-w-full bg-transparent text-gray-700 relative"
                >
                  Reports
                </Button>
              </div>

            </div>
          </div>
        </div>
      </aside>
    </div>
  );
};

export default SideBar;
