import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";

const Sidebar = () => {
  const location = useLocation();
  const [selected, setSelected] = useState("");

  useEffect(() => {
    const path = location.pathname.split("/")[1];
    setSelected(path.toLowerCase());
  }, [location]);

  const buttonClass = (buttonName) =>
    buttonName === selected ? "bg-kfupmgreen text-white" : "text-kfupmdarkgray";

  return (
    <div className="h-screen w-64 bg-white border-r border-kfupmlightgray flex flex-col">
      <div className="flex items-center justify-center p-6 border-b border-kfupmlightgray">
        <div className="h-12 w-12 bg-kfupmgreen rounded-full flex items-center justify-center text-white font-bold text-xl">
          K
        </div>
        <span className="ml-4 text-xl font-semibold text-kfupmgreen">KFUPM Clinic</span>
      </div>
      <nav className="flex-1 flex flex-col p-4 space-y-4">
        <Link to="/schedule-appointment">
          <button
            className={`flex items-center w-full p-3 rounded-lg ${buttonClass("schedule-appointment")}`}
            onClick={() => setSelected("schedule-appointment")}
          >
            <span className="text-base font-medium">Schedule Appointment</span>
          </button>
        </Link>
        <Link to="/appointments">
          <button
            className={`flex items-center w-full p-3 rounded-lg ${buttonClass("appointments")}`}
            onClick={() => setSelected("appointments")}
          >
            <span className="text-base font-medium">Appointments</span>
          </button>
        </Link>
      </nav>
      <div className="p-4 border-t border-kfupmlightgray flex items-center">
        <div className="flex items-center">
          <div className="w-10 h-10 bg-kfupmlightgray rounded-full flex items-center justify-center text-kfupmdarkgray font-semibold">
            A
          </div>
          <div className="ml-3">
            <p className="text-sm font-medium text-kfupmdarkgray">Abdullah Alalawi</p>
            <p className="text-xs text-kfupmlightgray">Patient</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
