import React from "react";
import SideBar from "./SideBar";

const MainLayout = ({ title, children }) => {
  return (
    <div className="h-screen flex overflow-hidden">
      <SideBar />
      <div className="flex-grow flex flex-col overflow-hidden">
        <header className="border-b bg-white w-full" style={{ paddingBottom: '7px' }}>
          <div className="p-3 pr-8 pl-8 flex flex-col sm:flex-row sm:items-center sm:justify-between">
            <h1 className="font-medium text-base text-tarkeeznavy mb-2 sm:mb-0">{title}</h1>
          </div>
        </header>
        <div className="flex-1 h-0 overflow-auto bg-backgroundcolor">
          {children}
        </div>
      </div>
    </div>
  );
};

export default MainLayout;
